from django.core.management.base import BaseCommand
from web3 import Web3, HTTPProvider, WebsocketProvider

from witswap.models import Configuration, EthereumToWitnetSwap
from witswap.settings import INFURA_URL, EWIT_TOKEN_ADDRESS, WEB3_SOCKET_URL
from witswap.utils import create_ethereum_to_witnet_swap

w3 = Web3(HTTPProvider(INFURA_URL))


class Command(BaseCommand):
    def handle(self, *args, **options):
        configuration = Configuration.objects.get(id=1)
        from_block = configuration.ethereum_events_from_block

        contract = w3.eth.contract(EWIT_TOKEN_ADDRESS, abi=ABI)

        ws3 = Web3(WebsocketProvider(WEB3_SOCKET_URL))
        filter_builder = contract.events.Swap.build_filter()
        filter_builder.fromBlock = from_block
        filter_builder.toBlock = 'latest'
        filter_instance = filter_builder.deploy(ws3)

        events = filter_instance.get_all_entries()
        for event in events:
            print('Analyzing event: ' + event.transactionHash.hex())

            if EthereumToWitnetSwap.objects.filter(burn_ethereum_transaction_hash=event.transactionHash.hex()).exists():
                print('Discarding because the TX hash was already processed')
                continue

            create_ethereum_to_witnet_swap(event.args['witAddress'],
                                           event.args['total'],
                                           event.args['totalMinusFees'],
                                           event.args['sender'],
                                           event.transactionHash.hex())

            print('Swap created')

            configuration.ethereum_events_from_block = event.blockNumber + 1
            configuration.full_clean()
            configuration.save()


